import { Text, View } from "react-native";

const Bubble = ({hungry}) =>{
    const bubbleText = (hungry) => {
        let textcontent = "";
        if (hungry == 3) {
            textcontent = 'Feed me. Feed me. Feed me. I NEED FOOOOOOD (;´༎ຶД༎ຶ`)'
        } else if (hungry == 2){
            textcontent = 'can you give me some treat QAQ'
        } else if (hungry == 1){
            textcontent = '(*^^*)'
        } else if (hungry == 0){
            textcontent = 'awwwww! Too FULL! STOPPPP or I’ll EXPLODE!! (#ﾟДﾟ)'
        } else{
            textcontent = 'error'
        };
        return textcontent;
    }
    return(
        <View>
            <Text style={{flex: 4, fontSize:18, alignSelf: 'flex-start'}}>   {bubbleText(hungry)}</Text>
            <View style={{flex: 1, alignItems: 'center'}}>
                <View style={{flex: 1,backgroundColor:'blue'}}></View>
                <Text style={{flex: 1, fontSize:18, backgroundColor: 'green'}}>  /</Text> 
            </View>
        </View>
    )
}

export default Bubble;
