import React, {useEffect, useState} from 'react';
import {Text,View, StyleSheet} from 'react-native';
import Cat from './Cat';

const HomeScreen = () => {
    return(
        <View style={styles.container}>
            <Text style={styles.welcomeText}>Happy Monday!</Text>
            {/* add: change according to date */}
            <Cat />
        </View>  
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 10,
        alignItems: 'center',
        justifyContent: 'center',
        //add background paper shape here
      },
    welcomeText: {
        margin: 10,
    },
    cat: {
        fontSize: 50,
    },
})

export default HomeScreen;
