import { Text, View } from "react-native";

const Bubble = ({hungry}) =>{
    const bubbleText = (hungry) => {
        let textcontent = "";
        if (hungry == 3) {
            textcontent = 'Feed me. Feed me. Feed me. I NEED FOOOOOOD (;´༎ຶД༎ຶ`)'
        } else if (hungry == 2){
            textcontent = 'can you give me some treat QAQ'
        } else if (hungry == 1){
            textcontent = '(*^^*)'
        } else if (hungry == 0){
            textcontent = 'awwwww! Too FULL! STOPPPP or I’ll EXPLODE!! (#ﾟДﾟ)'
        } else{
            textcontent = 'error'
        };
        return textcontent;
    }
    return(
        <View>
            <Text style={{fontSize:18}}>   {bubbleText(hungry)}</Text>
            <View>
                <View style={{flex: 1}}></View>
                <Text style={{flex: 1, fontSize:18}}>  /</Text> 
            </View>
        </View>
    )
}

export default Bubble;
