import React, { useContext, useState } from 'react';
import { View, Text, Button, StyleSheet, FlatList} from 'react-native';

import profileContext from './profileContext';
import AddTodo from './AddToDo';

export default function TodoListScreen() {
  const [todo, setTodo] = useState('');
  const [todos, setTodos] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const {profile} = useContext(profileContext);

  const handleAddTodo = () => {
    console.log()
    if (todo.length > 0) {
      const newTodo = {
        id: Date.now().toString(),
        todo: todo,
      };
      setTodos([...todos, newTodo]);
      setTodo('');
    }
  };

  const handleDeleteTodo = id => {
    const filteredTodos = todos.filter(item => item.id !== id);
    setTodos(filteredTodos);
  };

  const updateTodo = (text) => {
    setTodo(text);
    console.log(todo)
    handleAddTodo();
  }

  return (
    <View style={styles.maincontainor}>
      <View style={styles.header}>
        <Text style={styles.headerText}>{profile.name} wants to remind u ≧ᴗ≦✧</Text>
      </View>
      <View style={styles.toDoList}>
        <FlatList
          style={styles.list}
          data={todos}
          keyExtractor={item => item.id}
          renderItem={({ item }) => (
            <View style={styles.todo}>
              <Text style={styles.todoText}>{item.todo}</Text>
              <Button
                title="Delete"
                onPress={() => handleDeleteTodo(item.id)}
              />
            </View>
          )}
        />
      </View>
      <View style={styles.footer}>
        <AddTodo updateTodo={updateTodo}/>
        <Text>{todo}</Text>
        <Text>currentValue={JSON.stringify(todos)}</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  maincontainor: {
    flex: 1,
    flexDirection: 'column',
  },
  header: {
    flex: 1,
    justifyContent:'flex-end',
    paddingLeft: 40,
    paddingBottom: 10,
  },
  headerText: {
    fontSize: 16,
  },
  toDoList: {
    flex: 6,
  },
  footer: {
    flex: 1.3,
    paddingLeft: 40
  },
  container: {
    //flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 30,
  },
  input: {
    width: '100%',
    height: 50,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    marginBottom: 20,
  },
  list: {
    width: '100%',
  },
  todo: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  todoText: {
    flex: 1,
    marginRight: 10,
  },
});
