import React, { useState, useEffect } from "react";
import {View, StyleSheet, Text, Button} from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage';

import NameTag from "./NameTag";
import Bubble from "./DialogBubble";
import Cat from "./Cat";
import CatFoodBowl from "./CatFoodBowl";
import NavigateButton from "./NavigateButton";
import DeadCat from "./DeadCat";

const CatSimulator = () => {
    const [catFood, setCatFood] = useState(4);
    const [hungry, setHungry] = useState(3);
    const [debugging] = useState(true);
    const [alive, setAlive] = useState(true);
    const [adoptDays, setAdoptDays] = useState(234)

    const getCatFood = async () => {
        try{
            const jsonValue = await AsyncStorage.getItem('CatFood')
            if (jsonValue==null){
                setCatFood(5)
            } else {
                const num = JSON.parse(jsonValue);
                setCatFood(num);
            }
        } catch (e) {
            console.log('error in getData ')
            console.dir(e)
        }
    }

    const storeCatFood = async (value) => {
        try {
          const jsonValue = JSON.stringify(value)
          await AsyncStorage.setItem('CatFood', jsonValue)
        } catch (e) {
          console.log("error in storeData ")
          console.dir(e)
        }
    }

    const resetCatFood = async () => {
        try {
          console.log('in clearData')
          await AsyncStorage.clear()
          const jsonValue = JSON.stringify(5)
          await AsyncStorage.setItem('CatFood', jsonValue)
          setCatFood(jsonValue)
        } catch(e) {
          console.log("error in clearData ")
          console.dir(e)
        }
  }

    useEffect(() => {getCatFood()}, [])


  return (
    <View style={styles.maincontainor} >
        <View style={styles.header}>
            <Text>Happy Monday :)</Text>
            <NameTag name='James' days={adoptDays} />
        </View>
        <View style={styles.containor}>
            {alive ? 
                <>
                <View style={styles.catDialog}>
                    <Bubble hungry={hungry} />
                </View>
                <View style={styles.cat}>
                    <Cat />
                </View>
                </>
            :
                <>
                <View style={styles.catDialog}>
                </View>
                <View style={styles.cat}>
                    <DeadCat name="Ceci" />
                </View>
                </>
            }
        </View>
        <View style={styles.catfood}>
            {debugging ?
            <Button title='reset' onPress={() => {resetCatFood()}} />
            :
            <></>}
            <CatFoodBowl catFood={catFood} onPress={() => {setCatFood(catFood - 1);
                                                           storeCatFood(catFood -1)}}/>
            {/* add function: update status of cat(how hungry), update cat dialog(appear for 2 seconds) */}
        </View>
        <View style={styles.footer}>
            <NavigateButton type={false} onPress={() => alert('ToDoList')} />
            <NavigateButton type={true} onPress={() => alert('FocusClock')} />
        </View>
    </View>
  )
}

const styles = StyleSheet.create({
    maincontainor: {
        flex: 1,
        flexDirection: 'column',
    },
    header: {
        flex: 3,
        alignItems: 'center',
        justifyContent: 'center',
    },
    containor: {
        flex: 10,
        flexDirection: 'column',
    },
    catDialog: {
        flex: 1,
        justifyContent: 'flex-end',
        alignSelf: 'center',
        marginLeft: 70,
    },
    cat: {
        flex: 3,
        alignItems: 'center',
    },
    catfood: {
        flex: 2,
    },
    footer: {
        flex: 2,
        flexDirection: 'row',
        justifyContent: 'space-evenly',
    },
})


export default CatSimulator
