import React, { useContext, useState, useEffect} from 'react';
import { View, Text, StyleSheet, Pressable} from 'react-native';

import profileContext from './profileContext';

export default function FocusClock({duration}) {
    const {profile} = useContext(profileContext);
    const [second, setSecond] = useState(0)
    const [minute, setMinute] = useState(0)
    const [time, setTime] = useState(5 * 1000)

    useEffect(() => {setTimeout(() => {setTime(time - 1000)}, 1000);
                     setFormatTime();
    }, [time]);

    const setFormatTime = (time) => {
      let totalSecond = parseInt(Math.floor(time / 1000));
      let minute = parseInt(Math.floor(totalSecond / 60));
      let second = parseInt(Math.floor(totalSecond % 60));

      return `${minute} : ${second}`;
    }

  return (
    <View style={styles.maincontainor}>
        <View style={styles.header}>
            <Text style={styles.headerText}>It's time to study with {profile.name} (ﾟoﾟ;;</Text>
        </View>
      <View style={styles.container}>
            <Text>{setFormatTime({time})}</Text>
      </View>
      <View style={styles.footer}>
        <Pressable onPress={() => {alert('start')}}
        >
            <Text>Start now!</Text>
        </Pressable>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  maincontainor: {
    flex: 1,
    flexDirection: 'column',
  },
  header: {
    flex: 1,
    justifyContent:'flex-end',
    paddingLeft: 40,
    paddingBottom: 10,
  },
  headerText: {
    fontSize: 16,
  },
  container: {
    flex: 6
  },
  footer: {
    flex: 1,
    alignItems: 'center'
  },
});
